            s = "comments-system-" + a;
        "blogger" == a ? $(this).addClass(s).show() : "disqus" == a ? ((e = document.createElement("script")).type = "text/javascript", e.async = !0, e.src = "//" + disqusShortname + ".disqus.com/embed.js", (document.getElementsByTagName("head")[0] || document.getElementsByTagName("body")[0]).appendChild(e), $("#comments, #gpluscomments").remove(), $(this).append('<div id="disqus_thread"/>').addClass(s).show()) : "facebook" == a ? ($("#comments, #gpluscomments").remove(), $(this).append(t).addClass(s).show()) : "hide" == a ? $(this).hide() : $(this).addClass("comments-system-default").show()
    })
});
//

//
(function(d, s, id) {
  var js, fjs = d.getElementsByTagName(s)[0];
  if (d.getElementById(id)) return;
  js = d.createElement(s); js.id = id;
  js.src = 'https://connect.facebook.net/en_US/sdk.js#xfbml=1&version=v3.0';
  fjs.parentNode.insertBefore(js, fjs);
}(document, 'script', 'facebook-jssdk'));
